INSERT INTO about_sections (title, content, display_order) VALUES ('Career Start and Collaborations', 'In 2015, Brapurple joined Dopester Music, where he worked alongside Hitz Magik, a sensational soul singer. This collaboration led to Dopester Music being nominated for Best Music Group by the Ghana Teens Choice Awards. Brapurple has been working autonomously as a professional musician for about two years, during which he has released three tracks.', 0);
INSERT INTO about_sections (title, content, display_order) VALUES ('Notable Projects and Achievements', 'Flees: His project titled ''Flees'' was featured on BBC Radio UK and Fresh Faves, highlighting his talent to a broader audience, particularly in the UK. ''Flees'' symbolizes ''ever readiness or looking sharp,'' and was crafted over eight months following his debut project, ''Love and Light,'' which featured Kwame Yesu. Drawn To You: He collaborated with London-based singer-songwriter Gabrielle Sey for the single ''Drawn To You,'' which has been praised for its soulful and spicy blend, showcasing Brapurple''s ability to create summer hits with a unique flavor.', 1);
INSERT INTO about_sections (title, content, display_order) VALUES ('Social and Musical Goals', 'Brapurple maintains a precise and conceited social life, keeping a closed circle of friends. His musical purpose is to elevate his hometown, Takoradi, in the Western Region of Ghana, onto the global music map.', 2);
INSERT INTO about_sections (title, content, display_order) VALUES ('Online Presence', 'Brapurple has a presence on platforms like SoundCloud, where fans can listen to his music, and Bandcamp, where he has shared his work under his stage name, further establishing his identity in the music industry.', 3);
INSERT INTO about_sections (title, content, display_order) VALUES (NULL, 'Brapurple''s work has been noted for its creativity and his ability to bring a unique, powerful feel to his music, making him a notable figure in Ghanaian music.', 4);
